module hssi_ss_1_altera_merlin_router_1921_vt7ftnq_default_decode
  #(
     parameter DEFAULT_CHANNEL = 7,
               DEFAULT_WR_CHANNEL = -1,
               DEFAULT_RD_CHANNEL = -1,
               DEFAULT_DESTID = 77 
   )
  (output [100 - 94 : 0] default_destination_id,
   output [88-1 : 0] default_wr_channel,
   output [88-1 : 0] default_rd_channel,
   output [88-1 : 0] default_src_channel
  );

  assign default_destination_id = 
    DEFAULT_DESTID[100 - 94 : 0];

  generate
    if (DEFAULT_CHANNEL == -1) begin : no_default_channel_assignment
      assign default_src_channel = '0;
    end
    else begin : default_channel_assignment
      assign default_src_channel = 88'b1 << DEFAULT_CHANNEL;
    end
  endgenerate

  generate
    if (DEFAULT_RD_CHANNEL == -1) begin : no_default_rw_channel_assignment
      assign default_wr_channel = '0;
      assign default_rd_channel = '0;
    end
    else begin : default_rw_channel_assignment
      assign default_wr_channel = 88'b1 << DEFAULT_WR_CHANNEL;
      assign default_rd_channel = 88'b1 << DEFAULT_RD_CHANNEL;
    end
  endgenerate

endmodule


